sys_variable_value.value:
(function execute(inputs, outputs) {
  try{
    var restEndpoint = gs.getProperty('x_snc_paypal_spoke.endpoint')+'/v2/checkout/orders';
    var paypalToken = inputs.access_token;
    var makeOrderRest = new sn_ws.RESTMessageV2();
    makeOrderRest.setEndpoint(restEndpoint);
    makeOrderRest.setHttpMethod("post");
    makeOrderRest.setRequestHeader(
      "content-type",
      "application/json"
    );
    makeOrderRest.setRequestHeader(
      "Authorization",
      "Bearer "+paypalToken
    );
    var supportedCurrencys = ['AUD', 'BRL', 'CAD', 'CNY', 'CZK', 'DKK', 'EUR', 'HKD', 'HUF', 'INR', 'ILS', 'JPY', 'MYR', 'MXN', 'TWD', 'NZD', 'NOK', 'PHP', 'PLN', 'GBP', 'RUB', 'SGD', 'SEK', 'CHF', 'THB', 'USD'];
    var inputCurrency = '';
    if(supportedCurrencys.indexOf(inputs.currency) > -1){
      inputCurrency = inputs.currency;
    }else{
      inputCurrency = 'USD';
    }

    var requestBody = {
      "intent": "CAPTURE",
      "purchase_units": [
        {
          "amount": {
            "currency_code": inputCurrency,
            "value": inputs.value
          }
        }
      ]
    }
    makeOrderRest.setRequestBody(JSON.stringify(requestBody));
    var response = makeOrderRest.execute();
    var httpStatus = response.getStatusCode();
    var responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
    if(httpStatus == '201'){
      var responseJson = JSON.parse(responseBody);
      outputs.redirect_url = responseJson.links[1].href;
    }

  }catch(e){
    gs.info('Error - create Order - Paypal spoke ' + e.message);
  }

})(inputs, outputs);

